ee:variables.ee:set-variable:
%dw 2.0
output application/json

var map = readUrl("classpath://dw/mapping_payload_sms_spiels.json", "application/json")
var keyword = vars.formattedSubscriberData.keyword default "ANY" 
var brand = upper(vars.brand.'brand-name') default "ANY"    
var spielType = vars.spielType default "system-error" 
---
{
  "sms-spiel": (
    map[keyword][brand][spielType]    
      default map["ANY"]["ANY"]["system-error"] 
  )
}
